function initItems(){var a=51.541216,t=-.095678,d="assets/json/items.json.txt";$.getJSON(d).done(function(a){fillVerwaltung(a)}).fail(function(a,t,d){console.log(d)})}function fillVerwaltung(a){for(var t=$("body").find(".items-list"),d=0;d<a.data.length;d++)t.append('<li><div class="item" id="'+a.data[d].id+'"><a href="#" class="image"><img src="'+a.data[d].gallery+'" alt=""></a><div class="wrapper"><a href="#" class="quick-preview" id="'+a.data[d].id+'" data-gallery="'+a.data[d].gallery+'" data-title="'+a.data[d].title+'" data-type="'+a.data[d].type+'"  data-category="'+a.data[d].category+'" data-location="'+a.data[d].location+'" data-aperture="'+a.data[d].aperture+'" data-date="'+a.data[d].date+'" data-focal="'+a.data[d].focal+'" data-iso="'+a.data[d].iso+'" data-rating="'+a.data[d].rating+'"><h3>'+a.data[d].title+"</h3></a><figure>"+a.data[d].category+'</figure></div><div class="col-md-12 item-footer"><div class="col-md-6"><a href="#" class="btn btn-default">Edit</a></div><div class="col-md-6"><a href="#" class="btn btn-red">Delete</a></div></div></div></li>')}$(document).ready(function(){initItems()});