function authenticateUser(){$(".login-box").find("form").on("submit",function(t){t.preventDefault();var e=$(this).attr("action");AjaxHandler.request({method:"POST",cache:!1,url:e,data:$(this).serializeObject(),success:function(t){AuthenticationHelper.runAuthenticationCheck(t)}})})}$(document).ready(function(){var t=$(document).height(),e=$(".header").height(),n=t-e;$("#page-canvas").height(n),authenticateUser()});